css:
.charges-table {
  font-family: Arial, Helvetica, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

.charges-table td, .charges-table th {
  border: 1px solid #ddd;
  padding: 8px;
}

.charges-table tr:nth-child(even){background-color: #f2f2f2;}

.charges-table tr:hover {background-color: #ddd;}

.charges-table th {
  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: #4CAF50;
  color: white;
}

/* progress bar */
.updateProgressBlock {
  width: 100%;
  background-color: grey;
}

.updateProgressBar {
  width: 1%;
  height: 30px;
  background-color: green;
}
