    enableItem( 'luminisAccountTab' );
    if ( $enableLuminisRoles == 'Y')
      enableItem( 'luminisRolesTab' );
    if ( $enableStaffDirectory == 'Y')
      enableItem( 'facultyStaffDirectoryTab' );
    if ( $enableActiveDirectory == 'Y')
      enableItem( 'activeDirectoryTab' );
    if ( $enableActiveDirectory == 'Y')
      enableItem( 'adSecurityGroupsTab' );
    if ( $enableGoogleApps == 'Y')
      enableItem( 'googleAppsTab' );

