        alert('You have QUERY-ONLY privileges to this form.  You will be unable to modify any values or perform any functions.', {type:"error", flash: true});
      } else {
        alert('This Banner ID does not have a Luminis account.', {type:"error", flash: true});
      }

    } else {
      if ( $enableLuminisRoles == 'Y')
        enableItem( 'luminisRolesTab' );
      if ( $privInd != 'Y' ) {
        disableItem( 'luminisChangePasswordButton' );
        disableItem( 'luminisSynchronizeAccountButton' );
        disableItem( 'luminisCreateAccountButton' );
        disableItem( 'luminisDeleteAccountButton' );
        disableItem( 'luminisQuestion' );
        disableItem( 'luminisAnswer' );

      } else {
        if ( $hasLuminisAccount == 'Y' && $activeLuminisAccount != 'Y' ) {
          alert('This user has an inactive Luminis account.', {type:"error", flash: true});
          disableItem( 'luminisChangePasswordButton' );
          disableItem( 'luminisAnswer' );
        } else {
          enableItem( 'luminisChangePasswordButton' );
          enableItem( 'luminisAnswer' );
        }
        disableItem( 'luminisCreateAccountButton' );
        disableItem( 'luminisQuestion' );
        enableItem( 'luminisSynchronizeAccountButton' );
        enableItem( 'luminisDeleteAccountButton' );

      }
    }

    if ( $isLuminisAdmin == 'Y' && $hasLuminisAccount == 'Y' ) {
      enableItem( 'luminisChangePasswordButton' );
      enableItem( 'luminisDeleteAccountButton' );
    } else {
      disableItem( 'luminisChangePasswordButton' );
      disableItem( 'luminisDeleteAccountButton' );
    }

} else {
